    private void boton_sumar2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_boton_sumar2ActionPerformed
        operador2.setText("+");
        if ("".equals(num1.getText())) {
            num1.setText("0");
        }
        if ("".equals(num2.getText())) {
            num2.setText("0");
        }
        resultado.setText(" " + op.suma(Double.parseDouble(num1.getText()), Double.parseDouble(num2.getText())));

    }//GEN-LAST:event_boton_sumar2ActionPerformed

